/*KALUHUR*/
jQuery("document").ready(function($){
  var kaluhur = $('#kaluhur').hide();
  $(window).scroll(function () {
    if ($(this).scrollTop() > 200) {
      kaluhur.slideDown('slow');
    }else{
      kaluhur.hide();
    }
  }); 
});
/*KALUHUR*/

/*MENU MOBILE*/
$(document).ready(function() {
  var menu_mobile = $("#menu_mobile"),
      konten_menu_mobile = $("#konten_menu_mobile"),
      konten_isi = $("#konten_isi"),
      header_menu = $("#header_menu"),
      kmm_menu_anakan = $(".kmm_menu_anakan");

  menu_mobile.click(function(){
    konten_menu_mobile.toggleClass("kmm_muka");
    konten_isi.toggleClass("ki_geser");
    menu_mobile.toggleClass("mm_muka");
    header_menu.toggleClass("hm_geser");
  });

  konten_isi.click(function(){
    konten_menu_mobile.removeClass("kmm_muka");
    konten_isi.removeClass("ki_geser");
    menu_mobile.removeClass("mm_muka");
    header_menu.removeClass("hm_geser");
  });

  kmm_menu_anakan.children('a').on('click', function(){
    $(this).toggleClass("kmm_ma_muka").next("ul").slideToggle(200).end().parent(".kmm_menu_anakan").siblings(".kmm_menu_anakan").children('a').removeClass("kmm_ma_muka").next("ul").slideUp(200);
  });
});
/*MENU MOBILE*/